import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import ApiStatus from "../ApiStatus";
import { useDeleteContact, UseFetchDetailContact } from "../hooks/ContactHooks";
import defaultImage from "./defaultPhoto";
import ImageComponent from "./ImageComponent";


const ContactDetails = () =>
{
    const {id} = useParams();
    if(!id) throw Error("House id not found");
    const ContactId = parseInt(id);

    const {data,status,isSuccess} = UseFetchDetailContact(ContactId);
    const deleteContactMutation = useDeleteContact();


    if(!isSuccess) return <ApiStatus status={status}/>
    if (!data) return <div>contact didn't found</div>

    const [width, setWidth] = useState(30); // Initial width of the image
    const [height, setHeight] = useState(20); // Initial height of the image

    const resizeImage = () => 
    {
      // Change the width and height values
      const newWidth = 50; // New width in pixels
      const newHeight = 40; // New height in pixels

      setWidth(newWidth);
      setHeight(newHeight);
    };

    return (
      <div className="row">
      <div className="col-6">
        <div className="row">
          <img
            className="img-fluid"
            src={data.photo ? data.photo : defaultImage}
            alt="contact pic"
            style={{ width: `${width}px`, height: `${height}px` }}

          />
          <button onClick = {resizeImage }> ResizeImage</button>



        </div>
        <div className="row mt-3">
          <div className="col-2">
            <Link
              className="btn btn-primary w-100"
              to={`/contact/edit/${data.id}`}
            >
              Edit
            </Link>
          </div>
          <div className="col-2">
            <button
              className="btn btn-danger w-100"
              onClick={() => {
                if (window.confirm("Are you sure?"))
                  deleteContactMutation.mutate(data);
              }}
            >
              Delete
            </button>
          </div>
        </div>
      </div>
      <div className="col-6">
        <div className="row mt-2">
          <h3 className="col-12">{data.firstName}</h3>
        </div>
        <div className="row">
          <h3 className="col-12">{data.lastName}</h3>
        </div>
        <div className="row">
          <h2 className="themeFontColor col-12">
            {(data.email)}
          </h2>
        </div>
        <div className="row">
          <h3><div className="col-12 mt-3">{data.phoneNumber}</div></h3>
        </div>

      </div>
    </div>
    );


}
export default ContactDetails;
